import React, { useEffect } from "react";
import './HexaOptions.scss'

export default function HexaOptions({text, color}) {
    useEffect(()=>{

        const root = document.querySelecto('.frame');
        root?.style.setProperty("--color9",color)
    },[])
    
    return (
        <>
            <div className="HexaOptions" >
                <div className="frame" style={{$color:color}}>
                    <div>
                        <p>{text}</p>
                    </div>
                </div>
            </div>
        </>
    )
}
