import React, { useEffect, useState } from "react";
import './Home.scss'

export default function Home() {

    const [scroll, setScroll] = useState([1, 2, 3, 4, 5, 6, 7])

    const scrollEvent = (e) => {
        if (e.deltaY > 0) {
            let value = scroll.shift()
            console.log(value)
            // console.log(scroll.push(value))
            setScroll((state)=>[...state,value])
            
        } else {
            let value = scroll.pop()
            console.log(value)
            // console.log(scroll.unshift(value))
            setScroll((state)=>[])
            
        }
    }

    console.log(scroll)

    return (
        <>
            <div className="Home">
                <div className="platform" onWheel={scrollEvent} >
                    <div className={"post post" + (scroll[0])}></div>
                    <div className={"post post" + (scroll[1])}></div>
                    <div className={"post post" + (scroll[2])}></div>
                    <div className={"post post" + (scroll[3])}></div>
                    <div className={"post post" + (scroll[4])}></div>
                    <div className={"post post" + (scroll[5])}></div>
                    <div className={"post post" + (scroll[6])}></div>
                </div>
            </div>
        </>
    )
}
