import { USER_LOGIN_REQUEST, USER_LOGIN_SUCCESS, USER_LOGIN_FAIL, USER_LOGIN_RESET } from '../constants/loginConstant'
import axios from 'axios'

export const loginAction = (data, setErr, navigate) => async (dispatch, getstate) => {
    try {
        dispatch({ type: USER_LOGIN_REQUEST })
        console.log(data)
        const config = {
            withCredentials: true,
            headers: {
                'Access-Control-Allow-Origin': '*',
                'Content-Type': 'application/json'
            }
        }
        const response = await axios.post('http://localhost:3001/login', data, config)
        console.log(response.data)

        switch (response.data.code) {
            case 401:
                setErr(response.data.message)
                break;

            case 403:
                setErr(response.data.message)
                break;

            case 200:
                // localStorage.setItem()
                break;
        }

        switch (response.data.userInfo.Role) {
            case 'NEWREGISTRATION':
                navigate('/new_registration')
                break;

            case 'FUMIGATION':
                navigate('/fumigation')
                break;

            case 'BROCAMP':
                navigate('/brocamp')
                break;

            case 'ADVISOR':
                navigate('/advisor')
                break;

            case 'MANAGER':
                navigate('/manager')
                break;
        }
        console.log()

        dispatch({ type: USER_LOGIN_SUCCESS, payload: response.data.userInfo })
    } catch (error) {

    }
}
