import React, { useState } from "react";
import './3WayToggleBtn.scss'

export default function ToggleSwitch() {
    const [toggle, setToggle] = useState()
    return (
        <>
            <div className={"ToggleSwitch " + (
                toggle === 'present' ? 'present' :
                    toggle === 'absent' ? 'absent' :
                        toggle === 'cancel' ? 'cancelled' : ''
            )}>
                <div className="track">
                    <span onClick={() => { setToggle('present') }}> <p> P </p> </span>
                    <span onClick={() => { setToggle('cancel') }}> <p> C </p> </span>
                    <span onClick={() => { setToggle('absent') }}> <p> A </p> </span>
                </div>
                <span className={"button " + (
                    toggle === 'present' ? 'moveLeft' :
                        toggle === 'absent' ? '' :
                            toggle === 'cancel' ? 'moveCenter' : ''
                )} > </span>
            </div>
        </>
    )
}
