import React, { useState,useEffect } from 'react'
import './Sidebar.scss'

export default function Sidebar({pages, current}) {

    const [bar, setbar] = useState(false)
    const [currentPage, setCurrentPage] = useState('')

    useEffect(()=>{
        setCurrentPage(current)
    },[])


    return (
        <>
            <div className={'page row ' + (bar ? 'bar-on' : 'bar-off')}>
                <div className='sidebar'>
                    <div className="top">
                        {pages.map((item,index)=>{
                            return (
                                <i key={index} className={'fas ' + (item.icon) + (current == item.name ? 'active' : '')}></i>
                                )
                            })}
                    </div>
                    <div className='arrow'>
                        <div className='cols'></div>
                        <div className='cols'></div>
                        <div className='shape'>
                            <i className={'fas ' + (bar ? 'fa-angle-double-left' : 'fa-angle-double-right')} onClick={() => { setbar(!bar) }}></i>
                        </div>
                    </div>
                    <div className="down"></div>
                </div>
                <div className={'navbar-info' + (bar ? ' move' : '')}>
                    {pages.map((item,index)=>{
                        return(
                            <h6 className='tabs' key={index}>{item.name}</h6>
                        )
                    })
                    }
                </div>
            </div>
            {/* <div className="body">
                <div className="body-card">
                
                </div>
            </div> */}
        </>
    )
}
