import React from 'react'
import './profilePhoto.css'
import Cropper from '../Cropper/Cropper';


export default function ({ imgSrc, setImgSrc }) {

    const photoUpload = e => {
        e.preventDefault();
        const reader = new FileReader();
        const file = e.target.files[0];
        reader.onloadend = () => {
            setImgSrc({
                file: file,
                imagePreviewUrl: reader.result
            });
        }
        reader.readAsDataURL(file);
    }


    return (
        <>
            <div className='PhotoUpload'>
                <div className='PhotoUpload-Inner'>
                    <label htmlFor="upload_image" className='fas' >
                        <div className='img-upload'>
                            <img src={imgSrc} alt="" />
                        </div>
                        <input type="file" onChange={photoUpload} name="crop_image" className="crop_image" id="upload_image" />
                    </label>
                </div>
            </div>
            <Cropper  />
        </>
    )
}
