import React, { useEffect, useState } from "react";
import './Home.scss'

export default function Home() {

    const images = [
        'https://backlightblog.com/images/2021/04/landscape-photography-header-2000x1310.jpg',
        'https://iso.500px.com/wp-content/uploads/2015/11/photo-129299193.jpg',
        'https://www.azocleantech.com/images/news/ImageForNews_26919_15786618897301054.png',
        'https://learn.zoner.com/wp-content/uploads/2018/06/landscape-photography-at-every-hour-part-i-landscapes-in-the-morning.jpg',
        'https://thumbs.dreamstime.com/b/sun-rays-mountain-landscape-5721010.jpg',
        'https://blog.britishmuseum.org/wp-content/uploads/2021/09/Intro-Moray.jpg',
        'https://images.adsttc.com/media/images/5f40/2eff/b357/65ba/0000/0124/large_jpg/Panorama8-2a.jpg?1598041843',
        'https://i1.adis.ws/i/canon/canon-pro-ambassador-exchange-landscapes-1-1140?w=1140&qlt=70&fmt=jpg&fmt.options=interlaced&bg=rgb(255,255,255)',
        'https://images.adsttc.com/media/images/5f90/a0b5/63c0/1756/a700/00cd/large_jpg/05lft03s31-2008-0613-1326d-jw._Credit_Jarle_W%C3%A6hler._Statens_vegvesen._(1).jpg?1603313837',
        'https://i1.adis.ws/i/canon/canon-pro-unusual-landscape-photography-techniques-1?w=1140&qlt=70&fmt=jpg&fmt.options=interlaced&bg=rgb(255,255,255)',
        'https://thumbor.thedailymeal.com/Pe--0AE9-dDdEMC0HmU2N93RGhg=//https://www.theactivetimes.com/sites/default/files/2020/07/08/HERO_landscapes_look_like_they_belong_on_another_planet_shutterstock.jpg'
    ]

    const sevenImages = images.slice(0,7)
    
    const [scroll, setScroll] = useState([1, 2, 3, 4, 5, 6, 7])

    const [level, setLevel] = useState()

    const scrollEvent = (e) => {
        if (e.deltaY > 0) {
            let value = scroll.shift()
            setScroll((state) => [...state, value])

            sevenImages.pop()
            sevenImages.push(images[level])
            setLevel(state=>state)


            console.log('going left to right')
        } else {
            let value = scroll.pop()
            setScroll((state) => [value, ...state])

        }
    }

    // useEffect(()=>{
    //     if(scroll[0]===1){

    //     }
    // },[scroll])

    return (
        <>
            <div className="Home">
                <div className="platform" onWheel={scrollEvent} >
                    <div className={"post post" + (scroll[0])}>
                        <img src={sevenImages[0]} alt="" />
                        <p>image 1</p>
                    </div>
                    <div className={"post post" + (scroll[1])}>
                        <img src={sevenImages[1]} alt="" />
                        <p>image 2</p>
                    </div>
                    <div className={"post post" + (scroll[2])}>
                        <img src={sevenImages[2]} alt="" />
                        <p>image 3</p>
                    </div>
                    <div className={"post post" + (scroll[3])}>
                        <img src={sevenImages[3]} alt="" />
                        <p>image 4</p>
                    </div>
                    <div className={"post post" + (scroll[4])}>
                        <img src={sevenImages[4]} alt="" />
                        <p>image 5</p>
                    </div>
                    <div className={"post post" + (scroll[5])}>
                        <img src={sevenImages[5]} alt="" />
                        <p>image 6</p>
                    </div>
                    <div className={"post post" + (scroll[6])}>
                        <img src={sevenImages[6]} alt="" />
                        <p>image 7</p>
                    </div>
                </div>
            </div>
        </>
    )
}
