import React, { useState } from 'react'
import './profilePhoto.css'
import Cropper from '../Cropper/Cropper';


export default function ({ imgSrc, setImgSrc, register, errors, rules }) {

    const [cropper, setCropper] = useState(false)
    const [result , setResult] = useState()

    const photoUpload = e => {
        e.preventDefault();
        const reader = new FileReader()
        reader.addEventListener('load', () =>
            setImgSrc(reader.result.toString() || ''),
        )
        reader.readAsDataURL(e.target.files[0])
        { e.target.files[0] && setCropper(true) }

    }


    return (
        <>
            <div className='PhotoUpload'>
                <div className='PhotoUpload-Inner'>
                    <label htmlFor="photo" className='fas' >
                        <div className='img-upload'>
                            <img src={result} alt="" />
                        </div>
                        <input type="file" accept='image/*' onChange={photoUpload} className="photo" id="photo" />
                    </label>
                </div>
            </div>
            {cropper && <Cropper imgSrc={imgSrc} setResult={setResult} />}
        </>
    )
}
