import React, { useState } from "react";
import '../Login/Login.css'
import { Modal, CloseButton } from 'react-bootstrap'
import img from '../../assets/images/6.jpg'

export default function Login() {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);


    return (
        <>
            <p className='btn border-0 bg-none p-0 m-0' onClick={handleShow}> Login </p>
            <Modal show={show} onHide={handleClose} >
                <Modal.Body>
                    <img className='login-image' src={img} alt="" />
                    <CloseButton onClick={handleClose} variant='white' />
                    <div className="input-div">
                    <form onSubmit={handleSubmit(onSubmit)}>

<div className={style.loginForm}>
  <div>
    <i className='fa-solid fa-user'></i>
    <input type="text" placeholder='username'
      className={errors.username && style.inputError}
      onFocus={() => { setErr('') }}
      {...register('username', {
        required: 'username is required',
        pattern: {
          value: /^[a-zA-Z]+\s[a-zA-Z]+$/,
          message: 'should contain first and last name'
        }
      })}
    />
    <ErrorMessage errors={errors} name="username"
      render={({ messages }) => {
        return messages ? Object.entries(messages).map(([type, message]) => (
          <p className={style.errormsg} key={type}>{message}</p>
        )) : null;
      }} />
  </div>
  <div>
    <i className='fa-solid fa-lock'></i>
    <input type="password" placeholder='password'
      onFocus={() => { setErr('') }}
      {...register('password', {
        required: 'password is required',
      })}
    />
    <ErrorMessage errors={errors} name="password"
      render={({ messages }) => {
        return messages ? Object.entries(messages).map(([type, message]) => (
          <p className={style.errormsg} key={type}>{message}</p>
        )) : null;
      }} />
  </div>
</div>
<div className={style.remember}>
  <input type="checkbox" />
  <label htmlFor="">Remember me</label>
  <Link to={'/home'}>Forgot password ?</Link>

</div>
<div className={style.submitDiv}>
  <button className={style.submitButton}>
    submit
  </button>
</div>
</form>
                    </div>
                </Modal.Body>
            </Modal>
        </>
    )
}
