import React from "react";
import { Form } from "react-bootstrap";
import '../RegistrationInput/RegistrationInput.css'

export default function ({as, type, name, label, register, errors, rules, required }) {
    return (
        <>
            <div className="custom-input">
                <Form.Group className='form-floating'>
                    <Form.Control as={as} type={type} name={name} id={name} placeholder={name} {...register(label, { ...rules })} />
                    {errors[label] && errors[label].type === 'required' && <p className="">{name} is required</p>}
                    {errors[label] && errors[label].type === 'maxLength' && <p className="">{name} should be have maximum of {rules.maxLength} characters</p>}
                    {errors[label] && errors[label].type === 'min' && <p className="mb-3 text-red-500 text-left">{name} should be contain atleast {rules.min} characters</p>}
                    {errors[label] && errors[label].type === 'pattern' && <p className="mb-3 text-red-500 text-left">{name} is invalid</p>}
                    <Form.Label htmlFor={name}>{label}
                    {required && <span className="reqStar">&nbsp;*</span>}
                    </Form.Label>
                </Form.Group>
            </div>
        </>
    )
}
