import React, { useEffect, useState } from "react";
import './Home.scss'

export default function Home() {

    const [scroll, setScroll] = useState(4)

    const scrollEvent = (e) => {
        if (e.deltaY > 0) {
            if (scroll === 0) {setScroll(7)} 
            else {setScroll(scroll - 1)}
        } else {
            if (scroll === 7) {setScroll(0)} 
            else {setScroll(scroll + 1)}
        }
    }

    console.log(scroll)

    return (
        <>
            <div className="Home">
                <div className="platform" onWheel={scrollEvent} >
                    <div className={"post post" + (scroll)}></div>
                    <div className={"post post" + (scroll)}></div>
                    <div className={"post post" + (scroll)}></div>
                    <div className={"post post" + (scroll)}></div>
